import React, { useState, useCallback, useEffect } from 'react';
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from '@react-google-maps/api';
import axios from 'axios';
import './App.css';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Common grocery items and their categories
const GROCERY_CATEGORIES = {
  fruits: ['apple', 'orange', 'banana', 'grapes', 'mango', 'strawberry', 'blueberry'],
  vegetables: ['carrot', 'potato', 'tomato', 'lettuce', 'onion', 'broccoli'],
  snacks: ['chips', 'cookies', 'chocolate', 'nuts', 'candy'],
  dairy: ['milk', 'cheese', 'yogurt', 'butter', 'eggs'],
  meat: ['chicken', 'beef', 'pork', 'fish', 'lamb'],
  bakery: ['bread', 'cake', 'pastry', 'donut', 'muffin']
};

// Major supermarket chains
const SUPERMARKET_CHAINS = [
  'coles',
  'woolworths',
  'aldi',
  'iga',
  'foodworks',
  'spudshed',
  'harris farm'
];

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [center, setCenter] = useState({ lat: 37.7749, lng: -122.4194 });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);
  const [category, setCategory] = useState('');

  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  const [map, setMap] = useState(null);

  const onLoad = useCallback(function callback(map) {
    setMap(map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newCenter = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setCenter(newCenter);
          setLoading(false);
        },
        (error) => {
          setError('Error getting location: ' + error.message);
          setLoading(false);
        }
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  };

  const determineCategory = (searchTerm) => {
    const lowerSearchTerm = searchTerm.toLowerCase();

    // Check if it's a supermarket chain
    if (SUPERMARKET_CHAINS.some(chain => lowerSearchTerm.includes(chain))) {
      return 'supermarket';
    }

    // Check grocery categories
    for (const [cat, items] of Object.entries(GROCERY_CATEGORIES)) {
      if (items.some(item => lowerSearchTerm.includes(item))) {
        return cat;
      }
    }

    return 'grocery'; // Default category
  };

  const searchGroceryItems = async () => {
    if (!searchTerm.trim()) {
      setError('Please enter a search term');
      return;
    }

    if (!center.lat || !center.lng) {
      setError('Please get your location first');
      return;
    }

    setLoading(true);
    setError('');

    const category = determineCategory(searchTerm);
    setCategory(category);

    try {
      const response = await axios.get('https://api.yelp.com/v3/businesses/search', {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_YELP_API_KEY}`
        },
        params: {
          term: category === 'supermarket' ? searchTerm : 'grocery',
          latitude: center.lat,
          longitude: center.lng,
          radius: 1000,
          categories: category === 'supermarket' ? 'grocery,supermarkets' : 'grocery,markets',
          sort_by: 'distance',
          limit: 20
        }
      });

      // Filter and sort results
      const sortedResults = response.data.businesses
        .filter(business => {
          if (category === 'supermarket') {
            return SUPERMARKET_CHAINS.some(chain => 
              business.name.toLowerCase().includes(chain)
            );
          }
          return true;
        })
        .sort((a, b) => a.distance - b.distance);

      setResults(sortedResults);
      setError('');
    } catch (error) {
      setError('Error fetching results: ' + (error.response?.data?.error?.description || error.message));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getCurrentLocation();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8 text-blue-600">BluePie</h1>

        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex flex-col gap-4 mb-4">
            <div className="flex gap-4">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search for grocery items or stores..."
                className="flex-1 p-2 border rounded-lg"
                onKeyPress={(e) => e.key === 'Enter' && searchGroceryItems()}
              />
              <button
                onClick={getCurrentLocation}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 disabled:bg-blue-300"
                disabled={loading}
              >
                {loading ? 'Loading...' : 'Get Location'}
              </button>
              <button
                onClick={searchGroceryItems}
                className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 disabled:bg-green-300"
                disabled={loading}
              >
                {loading ? 'Searching...' : 'Search'}
              </button>
            </div>

            <div className="text-sm text-gray-600">
              <p>Try searching for: fruits (apple, orange), vegetables, snacks, or supermarket names (Coles, Woolworths)</p>
            </div>
          </div>

          {error && <p className="text-red-500 mb-4">{error}</p>}

          {isLoaded && (
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={center}
              zoom={15}
              onLoad={onLoad}
              onUnmount={onUnmount}
            >
              {results.map((business) => (
                <Marker
                  key={business.id}
                  position={{
                    lat: business.coordinates.latitude,
                    lng: business.coordinates.longitude
                  }}
                  onClick={() => setSelectedStore(business)}
                />
              ))}

              {selectedStore && (
                <InfoWindow
                  position={{
                    lat: selectedStore.coordinates.latitude,
                    lng: selectedStore.coordinates.longitude
                  }}
                  onCloseClick={() => setSelectedStore(null)}
                >
                  <div className="p-2">
                    <h3 className="font-bold">{selectedStore.name}</h3>
                    <p>{selectedStore.location.address1}</p>
                    <p>Price Level: {selectedStore.price || 'N/A'}</p>
                    <p>Rating: {selectedStore.rating} ⭐</p>
                    <p>Distance: {(selectedStore.distance / 1000).toFixed(2)} km</p>
                    {selectedStore.phone && <p>Phone: {selectedStore.phone}</p>}
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          )}

          <div className="mt-6">
            {results.map((business) => (
              <div 
                key={business.id} 
                className="border-b py-4 hover:bg-gray-50 cursor-pointer" 
                onClick={() => setSelectedStore(business)}
              >
                <h3 className="text-xl font-semibold">{business.name}</h3>
                <p className="text-gray-600">{business.location.address1}</p>
                <p className="text-gray-600">Price Level: {business.price || 'N/A'}</p>
                <p className="text-gray-600">Rating: {business.rating} ⭐</p>
                <p className="text-gray-600">Distance: {(business.distance / 1000).toFixed(2)} km</p>
                {business.phone && <p className="text-gray-600">Phone: {business.phone}</p>}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
