import React, { useState, useCallback, useEffect, useMemo } from 'react';
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from '@react-google-maps/api';
import axios from 'axios';
import './App.css';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Common grocery items and their categories
const GROCERY_CATEGORIES = {
  fruits: ['apple', 'orange', 'banana', 'grapes', 'mango', 'strawberry', 'blueberry'],
  vegetables: ['carrot', 'potato', 'tomato', 'lettuce', 'onion', 'broccoli'],
  snacks: ['chips', 'cookies', 'chocolate', 'nuts', 'candy'],
  dairy: ['milk', 'cheese', 'yogurt', 'butter', 'eggs'],
  meat: ['chicken', 'beef', 'pork', 'fish', 'lamb'],
  bakery: ['bread', 'cake', 'pastry', 'donut', 'muffin']
};

// Major supermarket chains
const SUPERMARKET_CHAINS = [
  'coles',
  'woolworths',
  'aldi',
  'iga',
  'foodworks',
  'spudshed',
  'harris farm'
];

// Sort options
const SORT_OPTIONS = {
  distance: 'Distance (Nearest)',
  price: 'Price (Low to High)',
  rating: 'Rating (Highest)'
};

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);
  const [center, setCenter] = useState({ lat: -33.8688, lng: 151.2093 }); // Default to Sydney
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);
  const [category, setCategory] = useState('');
  const [sortBy, setSortBy] = useState('distance');
  const [priceFilter, setPriceFilter] = useState('all');
  const [ratingFilter, setRatingFilter] = useState('all');
  const [mapError, setMapError] = useState('');

  const { isLoaded, loadError } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['places']
  });

  useEffect(() => {
    if (loadError) {
      setMapError('Error loading Google Maps. Please check your API key.');
    }
  }, [loadError]);

  const onLoad = useCallback(function callback(map) {
    // Map is loaded
  }, []);

  const onUnmount = useCallback(function callback() {
    // Map is unmounted
  }, []);

  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newCenter = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setCenter(newCenter);
          setLoading(false);
        },
        (error) => {
          setError('Error getting location: ' + error.message);
          setLoading(false);
        }
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  };

  const determineCategory = (searchTerm) => {
    const lowerSearchTerm = searchTerm.toLowerCase();

    // Check if it's a supermarket chain
    if (SUPERMARKET_CHAINS.some(chain => lowerSearchTerm.includes(chain))) {
      return 'supermarket';
    }

    // Check grocery categories
    for (const [cat, items] of Object.entries(GROCERY_CATEGORIES)) {
      if (items.some(item => lowerSearchTerm.includes(item))) {
        return cat;
      }
    }

    return 'grocery'; // Default category
  };

  const applyFilters = useMemo(() => {
    return (results) => {
      let filtered = [...results];

      if (priceFilter !== 'all') {
        filtered = filtered.filter(business => {
          const priceLevel = business.price ? business.price.length : 0;
          return priceLevel <= parseInt(priceFilter);
        });
      }

      if (ratingFilter !== 'all') {
        filtered = filtered.filter(business => business.rating >= parseFloat(ratingFilter));
      }

      filtered.sort((a, b) => {
        switch (sortBy) {
          case 'distance':
            return a.distance - b.distance;
          case 'price':
            const priceA = a.price ? a.price.length : 4;
            const priceB = b.price ? b.price.length : 4;
            return priceA - priceB;
          case 'rating':
            return b.rating - a.rating;
          default:
            return 0;
        }
      });

      return filtered;
    };
  }, [sortBy, priceFilter, ratingFilter]);

  const searchGroceryItems = async () => {
    if (!searchTerm.trim()) {
      setError('Please enter a search term');
      return;
    }

    if (!center.lat || !center.lng) {
      setError('Please get your location first');
      return;
    }

    setLoading(true);
    setError('');

    const category = determineCategory(searchTerm);
    setCategory(category);

    try {
      const response = await axios.get('https://api.yelp.com/v3/businesses/search', {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_YELP_API_KEY}`
        },
        params: {
          term: category === 'supermarket' ? searchTerm : 'grocery',
          latitude: center.lat,
          longitude: center.lng,
          radius: 1000,
          categories: category === 'supermarket' ? 'grocery,supermarkets' : 'grocery,markets',
          sort_by: 'distance',
          limit: 20
        }
      });

      const initialResults = response.data.businesses
        .filter(business => {
          if (category === 'supermarket') {
            return SUPERMARKET_CHAINS.some(chain => 
              business.name.toLowerCase().includes(chain)
            );
          }
          return true;
        });

      setResults(initialResults);
      setFilteredResults(applyFilters(initialResults));
      setError('');
    } catch (error) {
      setError('Error fetching results: ' + (error.response?.data?.error?.description || error.message));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (results.length > 0) {
      setFilteredResults(applyFilters(results));
    }
  }, [results, applyFilters]);

  useEffect(() => {
    getCurrentLocation();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8 text-blue-600">BluePie</h1>

        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex flex-col gap-4 mb-4">
            <div className="flex gap-4">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search for grocery items or stores..."
                className="flex-1 p-2 border rounded-lg"
                onKeyPress={(e) => e.key === 'Enter' && searchGroceryItems()}
              />
              <button
                onClick={getCurrentLocation}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 disabled:bg-blue-300"
                disabled={loading}
              >
                {loading ? 'Loading...' : 'Get Location'}
              </button>
              <button
                onClick={searchGroceryItems}
                className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 disabled:bg-green-300"
                disabled={loading}
              >
                {loading ? 'Searching...' : 'Search'}
              </button>
            </div>

            <div className="flex flex-wrap gap-4">
              <div className="flex items-center gap-2">
                <label className="text-gray-700">Sort by:</label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="p-2 border rounded-lg"
                >
                  {Object.entries(SORT_OPTIONS).map(([value, label]) => (
                    <option key={value} value={value}>{label}</option>
                  ))}
                </select>
              </div>

              <div className="flex items-center gap-2">
                <label className="text-gray-700">Price:</label>
                <select
                  value={priceFilter}
                  onChange={(e) => setPriceFilter(e.target.value)}
                  className="p-2 border rounded-lg"
                >
                  <option value="all">All Prices</option>
                  <option value="1">$</option>
                  <option value="2">$$</option>
                  <option value="3">$$$</option>
                  <option value="4">$$$$</option>
                </select>
              </div>

              <div className="flex items-center gap-2">
                <label className="text-gray-700">Rating:</label>
                <select
                  value={ratingFilter}
                  onChange={(e) => setRatingFilter(e.target.value)}
                  className="p-2 border rounded-lg"
                >
                  <option value="all">All Ratings</option>
                  <option value="4.5">4.5+ ⭐</option>
                  <option value="4.0">4.0+ ⭐</option>
                  <option value="3.5">3.5+ ⭐</option>
                  <option value="3.0">3.0+ ⭐</option>
                </select>
              </div>
            </div>

            <div className="text-sm text-gray-600">
              <p>Try searching for: fruits (apple, orange), vegetables, snacks, or supermarket names (Coles, Woolworths)</p>
            </div>
          </div>

          {error && <p className="text-red-500 mb-4">{error}</p>}

          {mapError && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
              {mapError}
            </div>
          )}

          {!isLoaded ? (
            <div className="flex items-center justify-center h-96">
              <div className="text-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
                <p className="mt-4 text-gray-600">Loading map...</p>
              </div>
            </div>
          ) : (
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={center}
              zoom={15}
              onLoad={onLoad}
              onUnmount={onUnmount}
              options={{
                zoomControl: true,
                streetViewControl: false,
                mapTypeControl: false,
                fullscreenControl: true,
              }}
            >
              {filteredResults.map((business) => (
                <Marker
                  key={business.id}
                  position={{
                    lat: business.coordinates.latitude,
                    lng: business.coordinates.longitude
                  }}
                  onClick={() => setSelectedStore(business)}
                />
              ))}

              {selectedStore && (
                <InfoWindow
                  position={{
                    lat: selectedStore.coordinates.latitude,
                    lng: selectedStore.coordinates.longitude
                  }}
                  onCloseClick={() => setSelectedStore(null)}
                >
                  <div className="p-2">
                    <h3 className="font-bold">{selectedStore.name}</h3>
                    <p>{selectedStore.location.address1}</p>
                    <p>Price Level: {selectedStore.price || 'N/A'}</p>
                    <p>Rating: {selectedStore.rating} ⭐</p>
                    <p>Distance: {(selectedStore.distance / 1000).toFixed(2)} km</p>
                    {selectedStore.phone && <p>Phone: {selectedStore.phone}</p>}
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          )}

          <div className="mt-6">
            {filteredResults.map((business) => (
              <div 
                key={business.id} 
                className="border-b py-4 hover:bg-gray-50 cursor-pointer" 
                onClick={() => setSelectedStore(business)}
              >
                <h3 className="text-xl font-semibold">{business.name}</h3>
                <p className="text-gray-600">{business.location.address1}</p>
                <p className="text-gray-600">Price Level: {business.price || 'N/A'}</p>
                <p className="text-gray-600">Rating: {business.rating} ⭐</p>
                <p className="text-gray-600">Distance: {(business.distance / 1000).toFixed(2)} km</p>
                {business.phone && <p className="text-gray-600">Phone: {business.phone}</p>}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
